### SCRIPT DE SUPPRESSION DES FICHIERS PLATEFPORM ###################################################################################
																											 
#####################################################################################################################################
#####################################################################################################################################
# Supprimer les fichiers fichier201*.txt plus vieux que 4 jours #################################################################### 
#####################################################################################################################################
#####################################################################################################################################
$DataFolder = "D:\software\5.xxx\Production\"
$DataFile = "D:\software\5.xxx\Production\fichier201*.txt"
$Time = 4
Set-Location $DataFolder
Get-ChildItem $DataFile -Recurse -File | ?{$_.ModificationTime -lt (get-date).AddDays(-$Time)} | %{Remove-Item $_.FullName -Force}
